package com.postcodes.app;import com.postcodes.framework.MyLogger;public abstract class BasePOC  {    protected String input;    public BasePOC(String input) {        setInput(input);    }    public String getInput() {        return input;    }    public void setInput(String input) {        if (input==null || input.equals("")) {            MyLogger.ErrorLog(BasePOC.class, "Input is null!");            this.input=null;        }else {            input=input.trim();            input=input.replace(" ", "");            input = input.replaceAll("^\"|\"$", "").trim();            if (input.length() >= 6 && input.length() <= 8) {                input = input.substring(0, input.length() - 3).concat(" ").concat(input.substring(input.length() - 3));            }            this.input = input;        }    }}