package com.postcodes.framework;import java.io.FileInputStream;import java.io.IOException;import java.util.Properties;public class Configuration {    private static String rootPath = Thread.currentThread().getContextClassLoader().getResource("com/postcodes/framework").getPath().replace("target/classes", "src/main/java");    private static String appConfigPath = rootPath + "/ConfigurationApp.properties";    private Properties appProperties = new Properties();    protected Configuration() {        setAppProperties();    }    public static Configuration of() {        return new Configuration();    }    public Properties getAppProperties() {        return appProperties;    }    public void setAppProperties() {        try {            appProperties.load(new FileInputStream(appConfigPath));        } catch (IOException e) {            MyLogger.ErrorLog(Configuration.class, e.getMessage());        }    }}