package com.postcodes.helper;import com.postcodes.app.BasePOC;import com.postcodes.app.CountryRegion;import com.postcodes.app.Nearest;import com.postcodes.app.POCValidation;import java.util.Properties;public class Context extends BasePOC implements DialogProvider{    public Context(String input) {        super(input);    }    public static Context init(String input){        return new Context(input);    }    @Override    public Properties config() {        return DialogFactory.of(null).config();    }    @Override    public POCValidation validation() {        return DialogFactory.of(getInput()).validation();    }    @Override    public CountryRegion countryRegion() {        return DialogFactory.of(getInput()).countryRegion();    }    @Override    public Nearest nearestCode() {        return DialogFactory.of(getInput()).nearestCode();    }}